#include <wx/wx.h>
#include <wx/mstream.h>
#include <wx/filedlg.h>
#include <wx/textctrl.h>
#include <wx/splitter.h>
#include <wx/button.h>
#include <vector>
#include <cstdint>
#include <string>
#include <fstream>
#include <wx/base64.h>

class ImageDisplayDialog : public wxDialog {
public:
    ImageDisplayDialog(wxWindow* parent, const wxString& base64Image1, const wxString& base64Image2)
        : wxDialog(parent, wxID_ANY, "Image Display", wxDefaultPosition, wxSize(600, 360)) {
        
        // 初始化成员变量
        //m_leftBitmap = nullptr;
        //m_rightBitmap = nullptr;
        
        // Create main sizer
        wxBoxSizer* mainSizer = new wxBoxSizer(wxVERTICAL);
        
        // Create horizontal sizer for images with 50px spacing
        wxBoxSizer* imageSizer = new wxBoxSizer(wxHORIZONTAL);
        
        // Left image panel
        wxPanel* leftPanel = new wxPanel(this);
        wxBoxSizer* leftSizer = new wxBoxSizer(wxVERTICAL);
        m_leftBitmap = new wxStaticBitmap(leftPanel, wxID_ANY, wxNullBitmap);
        leftSizer->Add(m_leftBitmap, 1, wxEXPAND | wxALL, 5);
        leftPanel->SetSizer(leftSizer);
        
        // Right image panel
        wxPanel* rightPanel = new wxPanel(this);
        wxBoxSizer* rightSizer = new wxBoxSizer(wxVERTICAL);
        m_rightBitmap = new wxStaticBitmap(rightPanel, wxID_ANY, wxNullBitmap);
        rightSizer->Add(m_rightBitmap, 1, wxEXPAND | wxALL, 5);
        rightPanel->SetSizer(rightSizer);
        
        // Add images to sizer with 50px spacing
        imageSizer->Add(leftPanel, 1, wxEXPAND);
        imageSizer->AddSpacer(50); // 50 pixels spacing between images
        imageSizer->Add(rightPanel, 1, wxEXPAND);
        
        // Add image sizer to main sizer
        mainSizer->Add(imageSizer, 1, wxEXPAND | wxALL, 10);
        
        // Add OK button
        wxButton* okButton = new wxButton(this, wxID_OK, "OK");
        wxBoxSizer* buttonSizer = new wxBoxSizer(wxHORIZONTAL);
        buttonSizer->AddStretchSpacer();
        buttonSizer->Add(okButton, 0, wxALIGN_CENTER | wxALL, 10);
        buttonSizer->AddStretchSpacer();
        
        mainSizer->Add(buttonSizer, 0, wxEXPAND);
        
        //SetSizerAndFit(mainSizer);
        // 先设置布局，再强制设置窗口大小
        SetSizer(mainSizer);
        
        // Load and display images
        LoadAndDisplayImage(base64Image1, m_leftBitmap);
        LoadAndDisplayImage(base64Image2, m_rightBitmap);
        
        // Set minimum size
        //SetMinSize(wxSize(600, 360));
        //Maximize(); // Maximize the dialog to fill the screen
        
        // Center on parent
        //CentreOnParent();
    }

private:
    wxStaticBitmap* m_leftBitmap;
    wxStaticBitmap* m_rightBitmap;
    
    void LoadAndDisplayImage(const wxString& base64Image, wxStaticBitmap* bitmapCtrl) {
        if (base64Image.IsEmpty()) {
            bitmapCtrl->SetBitmap(wxNullBitmap);
            return;
        }
        
        wxMemoryBuffer buffer = wxBase64Decode(base64Image);
        if (buffer.GetDataLen() == 0) {
            bitmapCtrl->SetBitmap(wxNullBitmap);
            return;
        }
        
        wxMemoryInputStream stream(buffer.GetData(), buffer.GetDataLen());
        wxImage image;
        
        if (image.LoadFile(stream)) {
            wxBitmap bmp(image);
            if (bmp.IsOk()) {
                bitmapCtrl->SetBitmap(bmp);
            } else {
                bitmapCtrl->SetBitmap(wxNullBitmap);
            }
        } else {
            bitmapCtrl->SetBitmap(wxNullBitmap);
        }
    }
};

class MainFrame : public wxFrame {
public:
    MainFrame() : wxFrame(NULL, wxID_ANY, "Base64 Image Viewer", wxDefaultPosition, wxSize(960, 640)) {
        // Set larger default size
        SetMinSize(wxSize(400, 300));
        
        // Create menu bar
        wxMenuBar *menuBar = new wxMenuBar();
        
        // File menu
        wxMenu *fileMenu = new wxMenu();
        fileMenu->Append(ID_OPEN_IMAGE1, "&Open Image 1\tCtrl+1");
        fileMenu->Append(ID_OPEN_IMAGE2, "&Open Image 2\tCtrl+2");
        fileMenu->Append(ID_SHOW_IMAGES, "&Show Images\tCtrl+S");
        //fileMenu->Append(ID_UPDATE_FROM_TEXT, "&Update from Text\tCtrl+U");
        fileMenu->AppendSeparator();
        fileMenu->Append(wxID_EXIT, "&Exit\tAlt+F4");
        menuBar->Append(fileMenu, "&File");
        
        // Help menu
        wxMenu *helpMenu = new wxMenu();
        helpMenu->Append(wxID_ABOUT, "&About");
        menuBar->Append(helpMenu, "&Help");
        
        SetMenuBar(menuBar);
        
        // Create main splitter for text controls
        m_mainSplitter = new wxSplitterWindow(this, wxID_ANY);
        m_mainSplitter->SetMinimumPaneSize(100);
        
        // Create text controls with monospace font
        wxFont font(10, wxFONTFAMILY_TELETYPE, wxFONTSTYLE_NORMAL, wxFONTWEIGHT_NORMAL);
        
        m_textCtrl1 = new wxTextCtrl(m_mainSplitter, wxID_ANY, m_base64Image1, 
                                   wxDefaultPosition, wxDefaultSize, 
                                   wxTE_MULTILINE | wxTE_RICH | wxHSCROLL);  // | wxTE_DONTWRAP
        m_textCtrl1->SetFont(font);
        
        m_textCtrl2 = new wxTextCtrl(m_mainSplitter, wxID_ANY, m_base64Image2, 
                                   wxDefaultPosition, wxDefaultSize, 
                                   wxTE_MULTILINE | wxTE_RICH | wxHSCROLL);  // | wxTE_DONTWRAP
        m_textCtrl2->SetFont(font);
        
        // Split the window vertically
        m_mainSplitter->SplitVertically(m_textCtrl1, m_textCtrl2);
        m_mainSplitter->SetSashGravity(0.5); // Equal split
        
        // Add status bar
        CreateStatusBar();
        SetStatusText("Ready - Paste Base64 strings or open image files");
        
        // Bind events
        Bind(wxEVT_MENU, &MainFrame::OnOpenImage1, this, ID_OPEN_IMAGE1);
        Bind(wxEVT_MENU, &MainFrame::OnOpenImage2, this, ID_OPEN_IMAGE2);
        Bind(wxEVT_MENU, &MainFrame::OnShowImages, this, ID_SHOW_IMAGES);
        //Bind(wxEVT_MENU, &MainFrame::OnUpdateFromText, this, ID_UPDATE_FROM_TEXT);
        Bind(wxEVT_MENU, &MainFrame::OnExit, this, wxID_EXIT);
        Bind(wxEVT_MENU, &MainFrame::OnAbout, this, wxID_ABOUT);
        
        // Maximize the main window
        //Maximize();
        CentreOnParent();
    }

private:
    enum {
        ID_OPEN_IMAGE1 = wxID_HIGHEST + 1,
        ID_OPEN_IMAGE2,
        ID_SHOW_IMAGES,
        ID_UPDATE_FROM_TEXT
    };
    
    wxSplitterWindow* m_mainSplitter;
    wxTextCtrl* m_textCtrl1;
    wxTextCtrl* m_textCtrl2;
    wxString m_base64Image1 = "";
    wxString m_base64Image2 = "";
    
    // Open image 1
    void OnOpenImage1(wxCommandEvent& event) {
        OpenImage(m_textCtrl1, &m_base64Image1, "Open Image 1");
    }
    
    // Open image 2
    void OnOpenImage2(wxCommandEvent& event) {
        OpenImage(m_textCtrl2, &m_base64Image2, "Open Image 2");
    }
    
    // Common function to open and encode image
    void OpenImage(wxTextCtrl* textCtrl, wxString* base64Storage, const wxString& dialogTitle) {
        wxFileDialog openFileDialog(this, dialogTitle, "", "",
                                  "Image files (*.png;*.jpg;*.jpeg;*.bmp;*.gif)|*.png;*.jpg;*.jpeg;*.bmp;*.gif", 
                                  wxFD_OPEN|wxFD_FILE_MUST_EXIST);
        
        if (openFileDialog.ShowModal() == wxID_CANCEL)
            return;
        
        wxString filePath = openFileDialog.GetPath();
        
        // Read file as binary
        std::ifstream file(filePath.ToStdString(), std::ios::binary);
        if (!file) {
            wxMessageBox("Failed to open file for reading", "Error", wxOK | wxICON_ERROR);
            return;
        }
        
        // Get file size
        file.seekg(0, std::ios::end);
        size_t fileSize = file.tellg();
        file.seekg(0, std::ios::beg);
        
        // Read file data
        std::vector<char> buffer(fileSize);
        file.read(buffer.data(), fileSize);
        
        // Encode to Base64 using wxWidgets built-in function
        *base64Storage = wxBase64Encode(buffer.data(), fileSize);
        textCtrl->SetValue(*base64Storage);
        
        SetStatusText("Encoded: " + filePath);
    }
    
    // Update images from text controls
    void OnUpdateFromText(wxCommandEvent& event) {
        m_base64Image1 = m_textCtrl1->GetValue();
        m_base64Image2 = m_textCtrl2->GetValue();
        SetStatusText("Updated images from text content");
    }
    
    // Show both images in dialog
    void OnShowImages(wxCommandEvent& event) {
        // Update from text controls first
        m_base64Image1 = m_textCtrl1->GetValue();
        m_base64Image2 = m_textCtrl2->GetValue();
        
        if (m_base64Image1.IsEmpty() && m_base64Image2.IsEmpty()) {
            wxMessageBox("No images to display. Please encode or paste Base64 strings first.", 
                       "Information", wxOK | wxICON_INFORMATION);
            return;
        }
        
        ImageDisplayDialog* dialog = new ImageDisplayDialog(this, m_base64Image1, m_base64Image2);
        dialog->ShowModal();
        //dialog->Destroy();
    }

    // About dialog
    void OnAbout(wxCommandEvent& event) {
        wxMessageBox("Base64 Image Viewer\n\n"
                    "Features:\n"
                    "- Open and display image files\n"
                    "- Encode images to Base64\n"
                    "- Display encoded images\n\n"
                    "Version 1.0", 
                    "About Base64 Image Viewer", wxOK | wxICON_INFORMATION);
    }

    // Exit application
    void OnExit(wxCommandEvent& event) {
        Close(true);
    }
};

class MyApp : public wxApp {
public:
    virtual bool OnInit() {
        // Add image handlers
        wxImage::AddHandler(new wxPNGHandler);
        wxImage::AddHandler(new wxJPEGHandler);
        wxImage::AddHandler(new wxGIFHandler);
        wxImage::AddHandler(new wxBMPHandler);
        
        // Create and show main window
        MainFrame *frame = new MainFrame();
        frame->Show(true);
        return true;
    }
};

wxIMPLEMENT_APP(MyApp);